using SensorsMaster.AppSettings;
using System;
using Newtonsoft.Json;
using System.Windows;

namespace SensorsMaster.Device.Model
{
    public abstract class BoardElement
    {
        #region Properties
        [JsonIgnore]
        public Settings Settings => Settings.GetInstance();

        /// <summary>
        /// Autogenerated. Starts at 0.
        /// </summary>
        public int Id { get; set; }
        public Point Point { get; set; } 
        #endregion

        #region Constructors
        public BoardElement()
        {
            Point = new Point(0, 0);
        }
        public BoardElement(Point point) : this()
        {
            Point = point;
        }
        public BoardElement(double x, double y) : this(new Point(x, y))
        { }
        #endregion

        #region Methods
        public double Distance(BoardElement element)
        {
            return this.Distance(element.Point);
        }
        public double Distance(Point point)
        {
            return Point.Subtract(point, this.Point).Length;
        }
        #endregion
    }
}
